--
-- Create function `GetValidCountryCode`
--
SET GLOBAL log_bin_trust_function_creators = 1;
CREATE FUNCTION GetValidCountryCode (countrycode varchar(25), languagecode varchar(25), lessoncode varchar(25))
RETURNS varchar(25) CHARSET utf8mb4 COLLATE utf8mb4_unicode_ci
BEGIN

  SET @Ccode := countrycode;
  SET @Lcode := languagecode;
  SET @LeCode := lessoncode;
  SET @cnt := 0;

  SET @cnt = (SELECT
      COUNT('*')
    FROM Translation t
      INNER JOIN task_content tc
        ON t.item_id = tc.content_id
      INNER JOIN task_group tg
        ON tg.task_group_id = tc.task_group_id
      INNER JOIN task ta
        ON ta.task_id = tg.task_id
      INNER JOIN lesson_task lt
        ON lt.task_id = ta.task_id
      INNER JOIN lesson le
        ON le.lesson_id = lt.lesson_id
      INNER JOIN Country c
        ON c.code = t.country_code
    WHERE t.country_code = @Ccode
    AND t.language_code = @Lcode
    AND le.code = @LeCode);

  IF (@cnt = 0) THEN
    SET countrycode := (SELECT
        code2
      FROM country
      WHERE code = @Ccode LIMIT 1);
    SET @cnt = (SELECT
        COUNT('*')
      FROM Translation t
        INNER JOIN task_content tc
          ON t.item_id = tc.content_id
        INNER JOIN task_group tg
          ON tg.task_group_id = tc.task_group_id
        INNER JOIN task ta
          ON ta.task_id = tg.task_id
        INNER JOIN lesson_task lt
          ON lt.task_id = ta.task_id
        INNER JOIN lesson le
          ON le.lesson_id = lt.lesson_id
        INNER JOIN Country c
          ON c.code = t.country_code
      WHERE t.country_code = countrycode
      AND t.language_code = @Lcode
      AND le.code = @LeCode);
  END IF;

  IF (@cnt = 0) THEN
    SET countrycode := '';
  END IF;

  RETURN countrycode;

END
$$

