  SET @DIR = CASE WHEN sortorder = '' THEN 'DESC' ELSE sortorder END;
  SET @startindex = CONVERT(startindex, char);
  SET @pagesize = CONVERT(pagesize, char);
  SET @searchtextnew = '';

  IF searchtext != '' THEN
    SET @searchtextnew = CONCAT('WHERE t.name like ''', '%', searchtext, '%', '''');
